import Vue from 'vue'

export const EventBus = new Vue()

export function isValidJwt (jwt) {  
  if (!jwt || jwt.split('.').length < 3) {
    return false
  }
  const data = JSON.parse((jwt.split('.')[1]))
  const exp = new Date(data.exp * 1000) // JS deals with dates in milliseconds since epoch
  const now = new Date()
  return now < exp
}
